import { useContext, useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Details() {
    const { user } = useContext(UserContext);  // Get user data from context
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const updatePassword = (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error'
            });
            return;
        }

        fetch(`http://localhost:4000/users/update/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ newPassword: password })
        })
        .then(response => response.json())
        .then(data => {
            if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                Swal.fire({
                    title: 'Success',
                    text: data.message,
                    icon: 'success'
                });
                setPassword('');
                setConfirmPassword('');
            } else {
                Swal.fire({
                    title: 'Error',
                    text: data.message,
                    icon: 'error'
                });
            }
        })
        .catch(err => {
            Swal.fire({
                title: 'Error',
                text: 'Something went wrong while updating the password',
                icon: 'error'
            });
        });
    };

    if (!user) {
        return (
            <Container>
                <Row className="justify-content-center">
                    <Col md={6}>
                        <h2 className="mt-5 mb-4 text-center">Profile</h2>
                        <p>Loading user data...</p>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <Container>
            <Row className="justify-content-center">
                <Col md={6}>
                    <h2 className="mt-5 mb-4 text-center">Profile</h2>
                    <h5>Welcome, {user.firstName} {user.lastName}!</h5>  {/* Display full name */}
                    <p>Email: {user.email}</p>  {/* Display email */}

                    <h4 className="mt-5">Change Password</h4>
                    <Form onSubmit={updatePassword}>
                        <Form.Group className="mb-3" controlId="password">
                            <Form.Label>New Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Enter new password" 
                                value={password} 
                                onChange={e => setPassword(e.target.value)} 
                                required 
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="confirmPassword">
                            <Form.Label>Confirm New Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Confirm new password" 
                                value={confirmPassword} 
                                onChange={e => setConfirmPassword(e.target.value)} 
                                required 
                            />
                        </Form.Group>

                        <Button variant="primary" type="submit" className="w-100">
                            Update Password
                        </Button>
                    </Form>
                </Col>
            </Row>
        </Container>
    );
}
