import { useContext, useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Spinner } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Profile() {
    const { user } = useContext(UserContext);
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(true); // To track loading of user data

    // Simulate fetching user details if user is undefined
    useEffect(() => {
        if (!user) {
            setLoading(true);
            fetch('http://localhost:4000/users/details', {
                method: 'POST',
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            })
            .then(response => response.json())
            .then(data => {
                if (data && data.result) {
                    // Assuming you are using context to store user data
                    setLoading(false);
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: 'Failed to load user details',
                        icon: 'error'
                    });
                }
            })
            .catch(err => {
                Swal.fire({
                    title: 'Error',
                    text: 'An error occurred while loading the profile',
                    icon: 'error'
                });
            })
            .finally(() => setLoading(false));
        } else {
            setLoading(false);
        }
    }, [user]);

    const updatePassword = (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error'
            });
            return;
        }

        fetch(`http://localhost:4000/users/update/${user?.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ newPassword: password })
        })
        .then(response => response.json())
        .then(data => {
            if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                Swal.fire({
                    title: 'Success',
                    text: data.message,
                    icon: 'success'
                });
                setPassword('');
                setConfirmPassword('');
            } else {
                Swal.fire({
                    title: 'Error',
                    text: data.message || 'Password update failed',
                    icon: 'error'
                });
            }
        })
        .catch(err => {
            Swal.fire({
                title: 'Error',
                text: 'Something went wrong while updating the password',
                icon: 'error'
            });
        });
    };

    if (loading) {
        return (
            <Container className="d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
                <Spinner animation="border" variant="primary" />
                <span className="ms-3">Loading Profile...</span>
            </Container>
        );
    }

    if (!user) {
        return (
            <Container className="d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
                <p>Unable to load user details. Please try again later.</p>
            </Container>
        );
    }

    return (
        <Container>
            <Row className="justify-content-center">
                <Col md={6} lg={5}>
                    <h2 className="mt-5 mb-4 text-center">Profile</h2>
                    <h5>Welcome, {user?.firstName} {user?.lastName}!</h5>
                    <p><strong>Email:</strong> {user?.email}</p>

                    <h4 className="mt-5">Change Password</h4>
                    <Form onSubmit={updatePassword}>
                        <Form.Group className="mb-3" controlId="password">
                            <Form.Label>New Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Enter new password" 
                                value={password} 
                                onChange={e => setPassword(e.target.value)} 
                                minLength={6}
                                required 
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="confirmPassword">
                            <Form.Label>Confirm New Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Confirm new password" 
                                value={confirmPassword} 
                                onChange={e => setConfirmPassword(e.target.value)} 
                                minLength={6}
                                required 
                            />
                        </Form.Group>

                        <Button variant="primary" type="submit" className="w-100">
                            Update Password
                        </Button>
                    </Form>
                </Col>
            </Row>
        </Container>
    );
}
