import { useContext, useState } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Profile() {
    const { user } = useContext(UserContext);
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    // Check if user is null or missing
    if (!user) {
        return (
            <Container className="mt-5">
                <Row className="justify-content-center">
                    <Col md={6}>
                        <h2 className="text-center">Profile</h2>
                        <p className="text-center">You must be logged in to view this page.</p>
                    </Col>
                </Row>
            </Container>
        );
    }

    const updatePassword = (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error'
            });
            return;
        }

        fetch(`http://localhost:4000/users/update/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`
            },
            body: JSON.stringify({ newPassword: password })
        })
        .then(response => response.json())
        .then(data => {
            if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                Swal.fire({
                    title: 'Success',
                    text: data.message,
                    icon: 'success'
                });
                setPassword('');
                setConfirmPassword('');
            } else {
                Swal.fire({
                    title: 'Error',
                    text: data.message,
                    icon: 'error'
                });
            }
        })
        .catch(err => {
            Swal.fire({
                title: 'Error',
                text: 'Something went wrong while updating the password',
                icon: 'error'
            });
        });
    };

    return (
        <Container className="mt-5">
            <Row className="justify-content-center">
                <Col md={6}>
                    <Card>
                        <Card.Body>
                            <h2 className="text-center mb-4">Profile</h2>
                            <h5>Welcome, {user.firstName} {user.lastName}!</h5>
                            <p>Email: {user.email}</p>

                            <h4 className="mt-4">Change Password</h4>
                            <Form onSubmit={updatePassword}>
                                <Form.Group className="mb-3" controlId="password">
                                    <Form.Label>New Password</Form.Label>
                                    <Form.Control 
                                        type="password" 
                                        placeholder="Enter new password" 
                                        value={password} 
                                        onChange={e => setPassword(e.target.value)} 
                                        required 
                                    />
                                </Form.Group>

                                <Form.Group className="mb-3" controlId="confirmPassword">
                                    <Form.Label>Confirm New Password</Form.Label>
                                    <Form.Control 
                                        type="password" 
                                        placeholder="Confirm new password" 
                                        value={confirmPassword} 
                                        onChange={e => setConfirmPassword(e.target.value)} 
                                        required 
                                    />
                                </Form.Group>

                                <Button variant="primary" type="submit" className="w-100">
                                    Update Password
                                </Button>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
