import { Container } from 'react-bootstrap';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import AppNavbar from './components/AppNavbar';
import Banner from './pages/Banner';
import CourseCard from './components/CourseCard';
import Register from './pages/Register';
import Login from './pages/Login';
import Logout from './pages/Logout';
import ErrorPage from './pages/ErrorPage';
import { useState, useEffect } from 'react';
import { UserProvider } from './UserContext';  // Ensure UserProvider is used
import Course from './pages/Course';
import AddCourse from './pages/AddCourse';
import Profile from './pages/Profile'; // Added Profile Page

function App() {
  const [loading, setLoading] = useState(true); // Loading state

  const unsetUser = () => {
    localStorage.clear();
  };

  useEffect(() => {
    fetch("http://localhost:4000/users/details", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    })
    .then(result => result.json())
    .then(data => {
      if (data && data.result) {
        // User details successfully fetched, user data will be set in context
      } else {
        // Handle scenario where no user details are returned
      }
    })
    .catch(error => console.error("Error fetching user details:", error))
    .finally(() => setLoading(false)); // Set loading to false after API call
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <UserProvider> {/* Wrapping the entire app with UserProvider */}
      <Router>
        <Container fluid>
          <AppNavbar />
          <Routes>
            <Route path='/' element={<Banner />} />
            <Route path='/login' element={<Login />} />
            <Route path='/register' element={<Register />} />
            <Route path='/logout' element={<Logout />} />
            <Route path='/profile' element={<Profile />} /> {/* Profile Route */}
            <Route path='/courses' element={<Course />} />
            <Route path='/add-course' element={<AddCourse />} />
            <Route path='*' element={<ErrorPage />} />
          </Routes>
        </Container>
      </Router>
    </UserProvider>
  );
}

export default App;
