import { useContext, useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Profile() {
    const { user } = useContext(UserContext);
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [email, setEmail] = useState(user?.email || '');

    useEffect(() => {
        console.log(user);
    }, [user]);

    const updatePassword = (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error',
            });
            return;
        }

        fetch(`http://localhost:4000/users/updatePassword/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newPassword: password }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                    setPassword('');
                    setConfirmPassword('');
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the password',
                    icon: 'error',
                });
            });
    };

    const updateEmail = (e) => {
        e.preventDefault();

        fetch(`http://localhost:4000/users/updateEmail/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newEmail: email }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-EMAIL-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the email',
                    icon: 'error',
                });
            });
    };

    if (!user || !user.firstName) {
        return (
            <Container>
                <Row className="justify-content-center">
                    <Col md={6}>
                        <h2 className="mt-5 mb-4 text-center">Profile</h2>
                        <p>Loading user data...</p>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <Container fluid className="bg-light py-5">
            <Row className="justify-content-center">
                <Col md={10}>
                    <Card className="shadow-lg rounded-lg border-0">
                        <Card.Body>
                            <Row>
                                {/* LEFT SIDE - USER INFO */}
                                <Col md={4} className="border-end">
                                    <div className="text-center mb-4">
                                        <img
                                            src={user.profilePicture || 'https://via.placeholder.com/150'}
                                            alt="Profile"
                                            className="rounded-circle border border-4 border-secondary shadow-sm"
                                            width="150"
                                            height="150"
                                        />
                                    </div>

                                    <h2 className="text-center text-primary mb-4">
                                        Hello, {user.firstName} {user.lastName}!
                                    </h2>

                                    <div className="mb-4">
                                        <h4 className="text-center text-dark">User Information</h4>
                                        <p><strong>First Name:</strong> {user.firstName}</p>
                                        {user.middleName && <p><strong>Middle Name:</strong> {user.middleName}</p>}
                                        <p><strong>Last Name:</strong> {user.lastName}</p>
                                        <p><strong>Contact Number:</strong> {user.contactNumber || 'Not Provided'}</p>
                                        <p><strong>Email:</strong> {user.email}</p>
                                    </div>
                                </Col>

                                {/* RIGHT SIDE - UPDATE FORMS */}
                                <Col md={8} className="ps-4">
                                    {/* Update Email */}
                                    <div className="mt-4">
                                        <h4 className="text-center text-dark">Update Email</h4>
                                        <Form onSubmit={updateEmail}>
                                            <Form.Group className="mb-4" controlId="email">
                                                <Form.Label>New Email</Form.Label>
                                                <Form.Control
                                                    type="email"
                                                    placeholder="Enter new email"
                                                    value={email}
                                                    onChange={(e) => setEmail(e.target.value)}
                                                    required
                                                    className="rounded-pill shadow-sm"
                                                />
                                            </Form.Group>
                                            <Button
                                                variant="success"
                                                type="submit"
                                                className="w-100 py-2 rounded-pill shadow-lg"
                                            >
                                                Update Email
                                            </Button>
                                        </Form>
                                    </div>

                                    {/* Change Password */}
                                    <div className="mt-5">
                                        <h4 className="text-center text-dark">Change Password</h4>
                                        <Form onSubmit={updatePassword}>
                                            <Form.Group className="mb-4" controlId="password">
                                                <Form.Label>New Password</Form.Label>
                                                <Form.Control
                                                    type="password"
                                                    placeholder="Enter new password"
                                                    value={password}
                                                    onChange={(e) => setPassword(e.target.value)}
                                                    required
                                                    className="rounded-pill shadow-sm"
                                                />
                                            </Form.Group>

                                            <Form.Group className="mb-4" controlId="confirmPassword">
                                                <Form.Label>Confirm New Password</Form.Label>
                                                <Form.Control
                                                    type="password"
                                                    placeholder="Confirm new password"
                                                    value={confirmPassword}
                                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                                    required
                                                    className="rounded-pill shadow-sm"
                                                />
                                            </Form.Group>

                                            <Button
                                                variant="primary"
                                                type="submit"
                                                className="w-100 py-2 rounded-pill shadow-lg"
                                            >
                                                Update Password
                                            </Button>
                                        </Form>
                                    </div>
                                </Col>
                            </Row>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
