import { useContext, useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Profile() {
    const { user } = useContext(UserContext);  // Get user data from context
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [email, setEmail] = useState(user.email || '');
    const [contactNumber, setContactNumber] = useState(user.contactNumber || '');

    // Log user to ensure the data is being passed correctly
    useEffect(() => {
        console.log(user);  // Check if user contains firstName, lastName, email, and contactNumber
    }, [user]);

    const updatePassword = (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error',
            });
            return;
        }

        fetch(`http://localhost:4000/users/update/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newPassword: password }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                    setPassword('');
                    setConfirmPassword('');
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the password',
                    icon: 'error',
                });
            });
    };

    const updateEmail = (e) => {
        e.preventDefault();

        fetch(`http://localhost:4000/users/updateEmail/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newEmail: email }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-EMAIL-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the email',
                    icon: 'error',
                });
            });
    };

    const updateContactNumber = (e) => {
        e.preventDefault();

        fetch(`http://localhost:4000/users/updateContactNumber/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newContactNumber: contactNumber }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-CONTACT-NUMBER-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the contact number',
                    icon: 'error',
                });
            });
    };

    // Ensure the user object is populated
    if (!user || !user.firstName) {
        return (
            <Container>
                <Row className="justify-content-center">
                    <Col md={6}>
                        <h2 className="mt-5 mb-4 text-center">Profile</h2>
                        <p>Loading user data...</p>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <Container fluid className="bg-light py-5">
            <Row className="justify-content-center">
                <Col md={8}>
                    <Card className="shadow-lg rounded-lg overflow-hidden">
                        <Card.Body>
                            <div className="text-center mb-4">
                                <img
                                    src={user.profilePicture || 'https://img.freepik.com/premium-vector/spiderman-character-vector-illustration-cartoon_969863-361231.jpg'}
                                    alt="Profile"
                                    className="rounded-circle border border-4 border-primary shadow-sm hover-shadow"
                                    width="150"
                                    height="150"
                                />
                            </div>

                            <h2 className="text-center text-primary mb-3">
                                Welcome, {user.firstName} {user.lastName}!
                            </h2>
                            <p className="text-center text-muted mb-4">Email: {user.email}</p>
                            <p className="text-center text-muted mb-4">Username: {user.username}</p>
                            <p className="text-center text-muted mb-4">
                                Contact Number: {user.contactNumber}
                            </p>

                            <h4 className="mt-5 text-center text-info">Update Contact Number</h4>

                            <Form onSubmit={updateContactNumber}>
                                <Form.Group className="mb-4" controlId="contactNumber">
                                    <Form.Label className="text-dark">New Contact Number</Form.Label>
                                    <Form.Control
                                        type="text"
                                        placeholder="Enter new contact number"
                                        value={contactNumber}
                                        onChange={(e) => setContactNumber(e.target.value)}
                                        required
                                        className="rounded-pill shadow-sm"
                                    />
                                </Form.Group>

                                <Button
                                    variant="success"
                                    type="submit"
                                    className="w-100 py-2 rounded-pill shadow-lg border-0 transition-all duration-300 hover:bg-success hover:text-white"
                                >
                                    Update Contact Number
                                </Button>
                            </Form>

                            <h4 className="mt-5 text-center text-info">Update Email</h4>

                            <Form onSubmit={updateEmail}>
                                <Form.Group className="mb-4" controlId="email">
                                    <Form.Label className="text-dark">New Email</Form.Label>
                                    <Form.Control
                                        type="email"
                                        placeholder="Enter new email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                        className="rounded-pill shadow-sm"
                                    />
                                </Form.Group>

                                <Button
                                    variant="success"
                                    type="submit"
                                    className="w-100 py-2 rounded-pill shadow-lg border-0 transition-all duration-300 hover:bg-success hover:text-white"
                                >
                                    Update Email
                                </Button>
                            </Form>

                            <h4 className="mt-5 text-center text-info">Change Password</h4>

                            <Form onSubmit={updatePassword}>
                                <Form.Group className="mb-4" controlId="password">
                                    <Form.Label className="text-dark">New Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        placeholder="Enter new password"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                        className="rounded-pill shadow-sm"
                                    />
                                </Form.Group>

                                <Form.Group className="mb-4" controlId="confirmPassword">
                                    <Form.Label className="text-dark">Confirm New Password</Form.Label>
                                    <Form.Control
                                        type="password"
                                        placeholder="Confirm new password"
                                        value={confirmPassword}
                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                        required
                                        className="rounded-pill shadow-sm"
                                    />
                                </Form.Group>

                                <Button
                                    variant="primary"
                                    type="submit"
                                    className="w-100 py-2 rounded-pill shadow-lg border-0 transition-all duration-300 hover:bg-primary hover:text-white"
                                >
                                    Update Password
                                </Button>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
