import { Container } from 'react-bootstrap';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AppNavbar from './components/AppNavbar';
import Banner from './pages/Banner';
import Register from './pages/Register';
import Login from './pages/Login';
import Logout from './pages/Logout';
import ErrorPage from './pages/ErrorPage';
import { useState, useEffect } from 'react';
import { UserProvider, UserContext } from './UserContext'; // Import UserProvider and UserContext
import Course from './pages/Course';
import AddCourse from './pages/AddCourse';
import Profile from './pages/Profile'; // Profile page

function App() {
  const [loading, setLoading] = useState(true); // Loading state
  const { setUser } = useContext(UserContext); // Access setUser function from UserContext

  useEffect(() => {
    // Fetch user details
    fetch("http://localhost:4000/users/details", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    })
    .then(result => result.json())
    .then(data => {
      if (data && data.result) {
        // Set user data in context
        setUser({
          id: data.result._id,
          firstName: data.result.firstName,
          lastName: data.result.lastName,
          email: data.result.email,
          isAdmin: data.result.isAdmin
        });
      } else {
        // Handle case where no user is found or error
        setUser({
          id: null,
          firstName: '',
          lastName: '',
          email: '',
          isAdmin: null
        });
      }
    })
    .catch(error => console.error("Error fetching user details:", error))
    .finally(() => setLoading(false)); // Stop loading after API call
  }, [setUser]); // `setUser` function from context

  if (loading) {
    return <p>Loading...</p>; // Show loading state until user is fetched
  }

  return (
    <UserProvider> {/* Wrap the app with UserProvider */}
      <Router>
        <Container fluid>
          <AppNavbar />
          <Routes>
            <Route path='/' element={<Banner />} />
            <Route path='/login' element={<Login />} />
            <Route path='/register' element={<Register />} />
            <Route path='/logout' element={<Logout />} />
            <Route path='/profile' element={<Profile />} /> {/* Profile Route */}
            <Route path='/courses' element={<Course />} />
            <Route path='/add-course' element={<AddCourse />} />
            <Route path='*' element={<ErrorPage />} />
          </Routes>
        </Container>
      </Router>
    </UserProvider>
  );
}

export default App;
