import { Container } from 'react-bootstrap';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

import AppNavbar from './components/AppNavbar';
import Banner from './pages/Banner';
import CourseCard from './components/CourseCard';
import Register from './pages/Register';
import Login from './pages/Login';
import Logout from './pages/Logout';
import ErrorPage from './pages/ErrorPage';
import { useState, useEffect } from 'react';
import { UserProvider } from './UserContext';
import Course from './pages/Course';
import AddCourse from './pages/AddCourse';
import Profile from './pages/Profile'; // Added Profile Page

function App() {
  // State for user details and loading state
  const [user, setUser] = useState({
    id: null,
    firstName: '',
    lastName: '',
    email: '',
    isAdmin: null
  });
  const [loading, setLoading] = useState(true); // Loading state

  // Function to unset user (clear localStorage)
  const unsetUser = () => {
    localStorage.clear();
  };

  useEffect(() => {
    // Fetch user details from the backend
    fetch("http://localhost:4000/users/details", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`  // Ensure token is available
      }
    })
      .then(result => result.json())
      .then(data => {
        if (data && data.result) {
          setUser({
            id: data.result._id,
            firstName: data.result.firstName,  // Ensure these are in the response
            lastName: data.result.lastName,
            email: data.result.email,
            isAdmin: data.result.isAdmin
          });
        } else {
          setUser({
            id: null,
            firstName: '',
            lastName: '',
            email: '',
            isAdmin: null
          });
        }
      })
      .catch(error => {
        console.error("Error fetching user details:", error);
        setUser({
          id: null,
          firstName: '',
          lastName: '',
          email: '',
          isAdmin: null
        });
      })
      .finally(() => setLoading(false));  // Set loading to false after the API call
  }, []);  // Empty dependency array ensures this runs once after the component mounts

  // Show loading screen if the user data is still being fetched
  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <UserProvider value={{ user, setUser, unsetUser }}>
      <Router>
        <Container fluid>
          <AppNavbar />
          <Routes>
            <Route path='/' element={<Banner />} />
            <Route path='/login' element={<Login />} />
            <Route path='/register' element={<Register />} />
            <Route path='/logout' element={<Logout />} />
            <Route path='/profile' element={<Profile />} /> {/* Profile route */}
            <Route path='/courses' element={<Course />} />
            <Route path='/add-course' element={<AddCourse />} />
            <Route path='*' element={<ErrorPage />} />
          </Routes>
        </Container>
      </Router>
    </UserProvider>
  );
}

export default App;
