import { useContext, useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Spinner } from 'react-bootstrap'; // Added Spinner for loading state
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Profile() {
    const { user } = useContext(UserContext);  // ‚úÖ Use the user context for session info
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false); // üîç Added loading state

    const updatePassword = (e) => {
        e.preventDefault();

        // üîç Validate password length
        if (password.length < 6) {
            Swal.fire({
                title: 'Error',
                text: 'Password must be at least 6 characters long',
                icon: 'error'
            });
            return;
        }

        // üîç Check if passwords match
        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error'
            });
            return;
        }

        setIsLoading(true); // üîç Set loading state to true to prevent multiple submissions

        fetch(`http://localhost:4000/users/update-password`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`  // üîç Token in header for security
            },
            body: JSON.stringify({ newPassword: password })  // üîç Do not send the user ID, extract it from the JWT on the server
        })
        .then(response => response.json())
        .then(data => {
            setIsLoading(false); // üîç Reset loading state

            if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                Swal.fire({
                    title: 'Success',
                    text: data.message,
                    icon: 'success'
                });
                setPassword('');
                setConfirmPassword('');
            } else {
                Swal.fire({
                    title: 'Error',
                    text: data.message,
                    icon: 'error'
                });
            }
        })
        .catch(err => {
            setIsLoading(false); // üîç Reset loading state
            Swal.fire({
                title: 'Error',
                text: 'Something went wrong while updating the password',
                icon: 'error'
            });
        });
    };

    // üîç Show a loading spinner while user info is being fetched
    if (!user) {
        return (
            <Container>
                <Row className="justify-content-center">
                    <Col md={6}>
                        <h2 className="mt-5 mb-4 text-center">Profile</h2>
                        <Spinner animation="border" role="status">
                            <span className="visually-hidden">Loading...</span>
                        </Spinner>
                        <p>Loading user data...</p>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <Container>
            <Row className="justify-content-center">
                <Col md={6}>
                    <h2 className="mt-5 mb-4 text-center">Profile</h2>
                    <h5>Welcome, {user.firstName} {user.lastName}!</h5>  {/* üîç Display the user's full name */}
                    <p>Email: {user.email}</p>  {/* üîç Display email */}

                    <h4 className="mt-5">Change Password</h4>
                    <Form onSubmit={updatePassword}>
                        <Form.Group className="mb-3" controlId="password">
                            <Form.Label>New Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Enter new password" 
                                value={password} 
                                onChange={e => setPassword(e.target.value)} 
                                required 
                            />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="confirmPassword">
                            <Form.Label>Confirm New Password</Form.Label>
                            <Form.Control 
                                type="password" 
                                placeholder="Confirm new password" 
                                value={confirmPassword} 
                                onChange={e => setConfirmPassword(e.target.value)} 
                                required 
                            />
                        </Form.Group>

                        <Button 
                            variant="primary" 
                            type="submit" 
                            className="w-100" 
                            disabled={isLoading} // üîç Disable button during loading
                        >
                            {isLoading ? 'Updating...' : 'Update Password'}
                        </Button>
                    </Form>
                </Col>
            </Row>
        </Container>
    );
}
