import { useContext } from 'react';
import { Navbar, Container, Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import UserContext from '../UserContext';

export default function AppNavbar() {
  const { user } = useContext(UserContext);
  const { id, isAdmin } = user || {}; // ✅ Destructure user for cleaner logic

  return (
    <Navbar expand="lg" className="bg-body-tertiary sticky-top shadow">
      <Container>
        <Navbar.Brand as={Link} to="/" className="fw-bold">
          UA ENROLLMENT
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/">Home</Nav.Link>
            <Nav.Link as={Link} to="/courses">Courses</Nav.Link>

            {id ? (
              <>
                {isAdmin && (
                  <Nav.Link as={Link} to="/add-course">Add Course</Nav.Link> // ✅ Admins see "Add Course"
                )}
                <Nav.Link as={Link} to="/profile">Profile</Nav.Link> // ✅ All logged-in users see "Profile"
                <Nav.Link as={Link} to="/logout">Logout</Nav.Link> // ✅ All logged-in users see "Logout"
              </>
            ) : (
              <>
                <Nav.Link as={Link} to="/register">Register</Nav.Link> 
                <Nav.Link as={Link} to="/login">Login</Nav.Link> // ✅ Guests see "Login"
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
