import React, { useState, useEffect } from "react";

// Create UserContext with default values
const UserContext = React.createContext({
  user: { id: null, isAdmin: null },
  setUser: () => {},
  unsetUser: () => {}
});

// UserProvider to manage user state
export const UserProvider = ({ children }) => {
  const [user, setUser] = useState({ id: null, isAdmin: null });

  useEffect(() => {
    // Check if user data exists in localStorage
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData) {
      setUser(userData);  // Set user if available
    }
  }, []);

  const logout = () => {
    // Remove user data from localStorage and set user state to null
    localStorage.removeItem('user');
    setUser({ id: null, isAdmin: null });
  };

  return (
    <UserContext.Provider value={{ user, setUser, unsetUser: logout }}>
      {children}
    </UserContext.Provider>
  );
};

export default UserContext;
