import { useContext, useState, useEffect } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import Swal from 'sweetalert2';
import UserContext from '../UserContext';

export default function Profile() {
    const { user } = useContext(UserContext);  // Get user data from context
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [email, setEmail] = useState(user?.email || '');  // Default to user email or empty string

    useEffect(() => {
        console.log(user);  // Check if user contains firstName, lastName, email, contactNumber
    }, [user]);

    const updatePassword = (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            Swal.fire({
                title: 'Error',
                text: 'Passwords do not match',
                icon: 'error',
            });
            return;
        }

        fetch(`http://localhost:4000/users/updatePassword/${user.id}`, {  // Ensure the correct route here
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newPassword: password }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-PASSWORD-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                    setPassword('');
                    setConfirmPassword('');
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the password',
                    icon: 'error',
                });
            });
    };

    const updateEmail = (e) => {
        e.preventDefault();

        fetch(`http://localhost:4000/users/updateEmail/${user.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
            body: JSON.stringify({ newEmail: email }),
        })
            .then((response) => response.json())
            .then((data) => {
                if (data.code === 'USER-EMAIL-SUCCESSFULLY-UPDATED') {
                    Swal.fire({
                        title: 'Success',
                        text: data.message,
                        icon: 'success',
                    });
                } else {
                    Swal.fire({
                        title: 'Error',
                        text: data.message,
                        icon: 'error',
                    });
                }
            })
            .catch((err) => {
                Swal.fire({
                    title: 'Error',
                    text: 'Something went wrong while updating the email',
                    icon: 'error',
                });
            });
    };

    // Ensure the user object is populated
    if (!user || !user.firstName) {
        return (
            <Container>
                <Row className="justify-content-center">
                    <Col md={6}>
                        <h2 className="mt-5 mb-4 text-center">Profile</h2>
                        <p>Loading user data...</p>
                    </Col>
                </Row>
            </Container>
        );
    }

    return (
        <Container fluid className="bg-light py-5">
            <Row className="justify-content-center">
                <Col md={8}>
                    <Card className="shadow-lg rounded-lg overflow-hidden border-0">
                        <Card.Body>
                            <div className="text-center mb-4">
                                <img
                                    src={user.profilePicture || 'https://scontent.fmnl25-6.fna.fbcdn.net/v/t39.30808-1/440233826_3610259385905161_3698015648048059644_n.jpg?stp=dst-jpg_s160x160_tt6&_nc_cat=102&ccb=1-7&_nc_sid=6738e8&_nc_ohc=jlclol-fFPAQ7kNvgHd-LIC&_nc_zt=24&_nc_ht=scontent.fmnl25-6.fna&_nc_gid=Aub08maoC5MX68d_R1-pW5e&oh=00_AYALNgWQVN3FC5dHhVTo17DenthO-Z0eyqvHYB2uJs-9aQ&oe=675DBEE9'}
                                    alt="Profile"
                                    className="rounded-circle border border-4 border-primary shadow-sm"
                                    width="200"
                                    height="200"
                                />
                            </div>

                            <h2 className="text-center text-primary mb-4">
                                Hello, {user.firstName} {user.lastName}!
                            </h2>

                            {/* User Information Section */}
                            <div className="mb-4">
                                <h4 className="text-center text-dark">User Information</h4>
                                <div className="text-center mb-3">
                                    <p><strong>First Name:</strong> {user.firstName}</p>
                                    {user.middleName && (
                                        <p><strong>Middle Name:</strong> {user.middleName}</p>
                                    )}
                                    <p><strong>Last Name:</strong> {user.lastName}</p>
                                    <p><strong>Contact Number:</strong> {user.contactNumber || 'Not Provided'}</p>
                                </div>
                                <p className="text-center"> <strong></strong>Email: {user.email}</p>
                            </div>

                            {/* Update Email */}
                            <div className="mt-4">
                                <h4 className="text-center text-dark">Update Email</h4>
                                <Form onSubmit={updateEmail}>
                                    <Form.Group className="mb-4" controlId="email">
                                        <Form.Label>New Email</Form.Label>
                                        <Form.Control
                                            type="email"
                                            placeholder="Enter new email"
                                            value={email}
                                            onChange={(e) => setEmail(e.target.value)}
                                            required
                                            className="rounded-pill shadow-sm"
                                        />
                                    </Form.Group>
                                    <Button
                                        variant="success"
                                        type="submit"
                                        className="w-100 py-2 rounded-pill shadow-lg border-0 transition-all duration-300 hover:bg-success hover:text-white"
                                    >
                                        Update Email
                                    </Button>
                                </Form>
                            </div>

                            {/* Change Password */}
                            <div className="mt-5">
                                <h4 className="text-center text-dark">Change Password</h4>
                                <Form onSubmit={updatePassword}>
                                    <Form.Group className="mb-4" controlId="password">
                                        <Form.Label>New Password</Form.Label>
                                        <Form.Control
                                            type="password"
                                            placeholder="Enter new password"
                                            value={password}
                                            onChange={(e) => setPassword(e.target.value)}
                                            required
                                            className="rounded-pill shadow-sm"
                                        />
                                    </Form.Group>

                                    <Form.Group className="mb-4" controlId="confirmPassword">
                                        <Form.Label>Confirm New Password</Form.Label>
                                        <Form.Control
                                            type="password"
                                            placeholder="Confirm new password"
                                            value={confirmPassword}
                                            onChange={(e) => setConfirmPassword(e.target.value)}
                                            required
                                            className="rounded-pill shadow-sm"
                                        />
                                    </Form.Group>

                                    <Button
                                        variant="primary"
                                        type="submit"
                                        className="w-100 py-2 rounded-pill shadow-lg border-0 transition-all duration-300 hover:bg-primary hover:text-white"
                                    >
                                        Update Password
                                    </Button>
                                </Form>
                            </div>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}
