import React, { useState, useEffect } from "react";

// Create context
const UserContext = React.createContext({
  user: { id: null, isAdmin: null, contactNumber: null, email: null }, // Initialize with default values
  setUser: () => {},
  unsetUser: () => {},
});

export const UserProvider = ({ children }) => {
  const [user, setUser] = useState({ id: null, isAdmin: null, contactNumber: null, email: null });

  // Fetch user data on component mount
  useEffect(() => {
    const token = localStorage.getItem('token'); // Check for token
    if (token) {
      fetch("http://localhost:4000/api/user-profile", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => response.json())
        .then((data) => {
          if (data.code === "USER-FOUND") {
            setUser(data.result); // Set user data to context
          }
        })
        .catch((error) => {
          console.error("Error fetching user profile:", error);
        });
    }
  }, []); // Runs once when component mounts

  // Function to unset user
  const unsetUser = () => {
    setUser({ id: null, isAdmin: null, contactNumber: null, email: null });
    localStorage.removeItem('token');
  };

  return (
    <UserContext.Provider value={{ user, setUser, unsetUser }}>
      {children}
    </UserContext.Provider>
  );
};

export default UserContext;
